import React, { useState } from 'react'
import './ToDo.css'
const ToDo = () => {
  const [inputData, setInputData] = useState('')
  const [items, setItems] = useState([])
  function sentenceCase (str) {
    if ((str===null) || (str===''))
         return false;
    else
     str = str.toString();
   
   return str.replace(/\w\S*/g,
  function(txt){return txt.charAt(0).toUpperCase() +
         txt.substr(1).toLowerCase();});
  }
  // item add function
  const addItem = () => {
    if (!inputData) {
      alert('Enter the item')
    } else {
      setItems([...items, inputData])
    }
  }
  return (
    <>
      <div className='main-div'>
        <div className='child-div'>
          <div className='head-hero'>
            <h3 className='head-hero1'>
              My <br></br>To-do <br></br>Application
            </h3>
            <i className='fa fa-list fa-4x head-hero2'></i>
          </div>
          <div className='add-items'>
            <input
              type='text'
              placeholder='✍️Add items now'
              className='form-control'
              value={inputData}
              onChange={event => setInputData(event.target.value)}
            />
            <i class='fa fa-plus add-btn' onClick={addItem}></i>
          </div>
          {/* show items */}
          <div className='show-item'>
            {items.map((curElem, index) => {
              return (
                <div className='each-item'>
                  <p>{sentenceCase(curElem)}</p>
                  <div className='todo-btn'>
                    <i class='fa fa-edit add-btn'></i>
                    <i class='fa fa-trash-alt add-btn'></i>
                  </div>
                </div>
              )
            })}
          </div>

          {/* remove button */}
          <div className='show-item'>
            <button className='btn effect04' data-sm-link-text='Remove All'>
              <span>Check List</span>
            </button>
          </div>
        </div>
      </div>
    </>
  )
}

export default ToDo
